CREATE TABLE "public"."users" ("id" serial NOT NULL, "uuid" uuid NOT NULL, "first_name" text NOT NULL, "last_name" text NOT NULL, "workflow_state" text NOT NULL DEFAULT 'pending', "email" text NOT NULL, "email_verified" boolean NOT NULL DEFAULT false, "phone_no" text NOT NULL, "phone_verified" boolean NOT NULL DEFAULT false, "deactivated_at" timestamptz, PRIMARY KEY ("id") , UNIQUE ("uuid"), UNIQUE ("email"), UNIQUE ("phone_no"));
